mapper.select:
select nation, day, seq, title, detail, image, tofrom, transport
from contentstb 
where nation=#{idx};

select idx, name, dust, continents, clickcnt, showcnt, customer, weight, speech,(select url from imagetb where type=#{type} AND nation=#{nationidx}) as url, price, type
from nationtb
where idx=#{nationidx};

select idx, name, dust, continents, clickcnt, showcnt, customer, weight, speech, price, type
from nationtb
where idx=#{nationidx};

mapper.update:
update nationtb 
set clickcnt=clickcnt+1
where idx=#{idx}

update nationtb 
set showcnt=showcnt+1
where idx=#{idx}
